// ----------------------------------------------------------------------
// HELPER FUNCTION
// ----------------------------------------------------------------------

export const getAllActivities = (lang: Language): Activity[] => {
  return fullRawList.map(act => {
    let translation: Partial<Activity> | undefined;

    if (lang === 'es') translation = activities_es[act.id];
    if (lang === 'el') translation = activities_el[act.id];
    if (lang === 'sv') translation = activities_sv[act.id];
    if (lang === 'it') translation = activities_it[act.id];

    if (translation) {
      // Ensure arrays are handled correctly if translation provided them, otherwise fallback to English arrays
      return { 
        ...act, 
        ...translation,
        // If the translation has arrays, use them, otherwise use the original english ones
        focusPoints: translation.focusPoints || act.focusPoints,
        examples: translation.examples || act.examples
      };
    }
    
    // Default to English
    return act;
  });
};
